html:
{% macro insert_letter_head() %}
{% if letter_head and not no_letterhead -%}
   <div class="section-break">{{ letter_head }}</div>
{%- endif %}
<div class="section-break">
    <table dir="rtl" class="table table-bordered table-condensed" style="height: 11px;">
    <tbody dir="rtl">
        <tr style="height: 11px;">
            <th class="text-right"  >{{ _("رقم الفاتورة") }}</th>
            <td class="text-right" >{{ doc.name }}</td>
            <th class="text-right"  >{{ _("الحالة") }}</th>
            <td class="text-right"  >{{ doc.rent_status }}</td>
        </tr>
    </tbody>
</table>
</div>
{% endmacro %}

{% macro insert_letter_footer() %}
    <div class="section-break">
        <table border="0" frame="box" dir="rtl" style="width: 100%;" class="inv-payment-details">
            <tbody dir="rtl">
                <tr>
                    <td style="width: 33%;">{{ _("الإجمالي") }} : {{ (doc.get_formatted('grand_total').split(' '))[1] }} د.ل</td>
                    <td style="width: 33%;">{{ _("المدفوع") }} : {{(doc.grand_total - doc.outstanding_amount)|round(2, 'floor') }} د.ل</td>
                    <td style="width: 33%;">{{ _("الباقي") }} : {{ (doc.get_formatted('outstanding_amount').split(' '))[1] }} د.ل</td>
            </tbody>
        </table>
    </div>
    {% if letter_head and not no_letterhead -%}
        <div class="section-break" style="margin-top:10px; font-size: 10pt;">
            {{ footer }}
        </div>
    {%- endif %}
{% endmacro %}

{% macro table_head() %}
<thead style="display: table-header-group;">
    <tr>
        <th class="text-center" >
            {{ _("البيان") }}
        </th>
        <th class="text-center" >
            {{ _("سعر الوحدة")}}
        </th>
        <th class="text-center" >
            {{ _("الكمية") }}
        </th>
        <th class="text-center" >
            {{ _("القيمة") }}
        </th>
    </tr>
</thead>
{% endmacro %}

{% macro insert_table_row(row) %}
<tr>
    <td >
        <div class="value">
            {{ row.item_name }}
        </div>
    </td>
    <td class="text-center" >
        <div class="value text-center">
            {{ ((row.get_formatted("rate", doc)).split(' '))[1] }} د.ل
        </div>
    </td>
    <td class="text-right" >
        <div class="value text-center">
            {{ row.qty }}
        </div>
    </td>
    <td class="text-center" >
        <div class="value text-center">
            {{ ((row.get_formatted("amount", doc)).split(' '))[1] }} د.ل
        </div>
    </td>
</tr>
{% endmacro %}
{{insert_letter_head()}}
<div class="section-break">
        <table dir="rtl" class="table table-bordered table-condensed" style="height: 11px;">
        <tbody dir="rtl">
            <tr style="height: 11px;">
                <th class="text-right"  >{{ _("اسم الزبون") }}</th>
                <td class="text-right"  >{{ doc.customer_name }}</td>
                <th class="text-right"  >{{ _("التاريخ") }}</th>
                <td class="text-right"  >{{ doc.posting_date }}</td>
            </tr>
            <tr style="height: 11px;">
                <th class="text-right"  >{{ _("تاريخ التسليم") }}</th>
                <td class="text-right"  >{{ doc.delivery_date }}</td>
                <th class="text-right"  >{{ _("تاريخ الإرجاع") }}</th>
                <td class="text-right"  >{{ doc.return_date }}</td>
            </tr>
        </tbody>
    </table>
</div>

{% set rows_step = namespace(value=0) %}
<div class="section-break">
    <table dir="rtl" class="table table-bordered table-condensed" 
    style="margin-top: 0" class="items-table">
        <!-- call table head -->
        {{table_head()}}
        <tbody>
        <!-- loop doc items -->
        {% for row in doc.items%}
            {% set rows_step.value = rows_step.value + 1 %}
            {% if loop.index == 4 and rows_step.value == 4 %}
                <!-- add row -->
                {{insert_table_row(row)}}
                {% set rows_step.value = 0 %}
                <!-- close table body -->
                </tbody>
                <!-- close table -->
                </table>
                <!-- close main div -->
                </div>
                {{insert_letter_footer()}}
                <!-- start new table -->
                {% if not loop.last %}
                    <div class="page-break"></div>
                    {{insert_letter_head()}}
                    <div class="section-break">
                    <table dir="rtl" class="table table-bordered table-condensed">
                        <!-- set table headers -->
                        {{table_head()}}
                        <tbody>
                {% endif %}
            {% elif loop.index and rows_step.value == 6 %}
                <!-- add row -->
                {{insert_table_row(row)}}
                {% set rows_step.value = 0 %}
                <!-- close table body -->
                </tbody>
                <!-- close table -->
                </table>
                <!-- close main div -->
                </div>
                {{insert_letter_footer()}}
                {% if not loop.last %}
                    <div class="page-break"></div>
                    {{insert_letter_head()}}
                    <div class="section-break">
                    <table dir="rtl" class="table table-bordered table-condensed">
                        <!-- set table headers -->
                        {{table_head()}}
                        <tbody>
                {% endif %}
            {% else %}
                {{insert_table_row(row)}}
                {% if loop.last %}
                    {{ insert_letter_footer() }}
                    </tbody>
                    </table>
                    </div>
                {% endif %}
            {% endif %}
    {% endfor %}
